var game = {
    status: false,
    images: {
        dir: 'resources/img/',
        label: 'you-',
        extension: '.gif',
        size: 8
    },
    dic: [],
    word: '',
    keysPad: null,
    beep: new Audio("data:audio/wav;base64,"),
    cursor: null
}

var user = {
    fails: 0,
    cursor: {
        char: '|',
        elementPos: null
    },
    score: 0
}


window.onload = function () {
    loadGame()
    listenEvents()
    console.log('[pageReady]')
}

function q(str) {
    console.log('[q]', str)
    return document.querySelectorAll(str)
}

function clickWordCharEvent() {
    console.log('c {click}', this)
    setCursor(this)
}

function listenEvents() {
    console.log('[listenEvents]')
    document.onselectstart = function()
    {
        return false;
    } 
    // Firefox
    document.onmousedown = function()
    {
        return false;
    }
    q('.wordChar').forEach(c => {
        c.addEventListener('click', clickWordCharEvent)
    })
    document.addEventListener('keypress', (event) => {
        const keyName = event.key
        console.log('document {keypress}', keyName)
        if( game.cursor != null) {
            setKey(keyName)
        } else {
            alert('Please first select the target char.')
        }
    })
}

function readTextFile(file) {
    let text = null
    let rawFile = new XMLHttpRequest()
    rawFile.open("GET", file, false)
    rawFile.onreadystatechange = function () {
        if (rawFile.readyState === 4 && rawFile.status === 200 || rawFile.status == 0)
            text = rawFile.responseText
    }
    rawFile.send(null)
    return text
}

function loadDic(path) {
    let dic = []
    let text
    try {
        text = readTextFile(path)
    } catch {
        alert('IF YOU DIDN\'T PLAYED THIS GAME OVER A SERVER THE DICTIONARY WILL NOT LOAD AND WILL USE AN INTERNALS ONE')
        text = 'javascript\nhola\nsol\ncolleja\npechuga\npollo\nlechuga'
    }
    text.split('\n').forEach(word => {
        dic.push(word)
    })
    return dic
}

function loadGame() {
    game.dic = loadDic('resources/data/spanish.dic')
    game.word = game.dic[Math.floor(Math.random() * game.dic.length)]
    game.keysPad = initKeysPad()
}

function initKeysPad() {
    var keysPad = []
    var wBase = q('.wordChar')[0]
    var wParent = wBase.parentElement
    wParent.removeChild(wBase)
    for (var i = 0; i < game.word.length; i++) {
        var wChar = game.word[i]
        var wId = parseInt(wBase.id) + 1
        var wBase = wBase.cloneNode()
        wBase.id = wId
        wParent.appendChild(wBase)
        keysPad.push(wBase)
    }
    return keysPad
}

function fail() {
    console.log('[fail]')
    user.fails++
    you.src = `${game.images.dir}${game.images.label}${user.fails}${game.images.extension}`
    if (user.fails == game.images.size) {
        gameOver(false)
    }
}

function setCursor(e) {
    console.log('[setCursor]', e)
    game.cursor = e
}

function setKey(keyName) {
    if (game.word[game.cursor.id] == keyName) {
        game.cursor.innerHTML = keyName
        game.cursor.removeEventListener('click', clickWordCharEvent)
        game.cursor = null
        success()
    } else {
        fail()
    }
}


function success() {
    user.score++
    beep()
    if (user.score == game.word.length) {
        gameOver(true)
    }
}

function beep() {
    game.beep.play();
}

function gameOver(status) {
    console.log('[gameOver]', status)
    let youGame = status ? 'win' : 'lose'
    alert(`[GAME OVER] You ${youGame}`)
    location.reload()
}
